package com.me.coresmodule.features.bot;

import com.me.coresmodule.utils.FilesHandler;
import com.me.coresmodule.utils.Helper;
import com.me.coresmodule.utils.ScreenshotUtils;
import com.me.coresmodule.utils.chat.Chat;
import com.me.coresmodule.utils.events.Register;
import net.minecraft.client.MinecraftClient;
import org.json.JSONObject;
import org.json.JSONArray;

import java.util.*;
import java.util.concurrent.TimeUnit;
import java.io.*;
import java.nio.file.*;

public class Bot {

    private static Process botProcess = null;

    private static Thread thread = new Thread(() -> {
        while (true) {
            try {
                String content = FilesHandler.getContent("bot/sharedForMod.json");
                content = content.trim();
                if (!content.isEmpty() && !content.equals("{}")) {
                    JSONObject json = new JSONObject(content);
                    String request = json.getString("request");

                    switch (request) {
                        case "screenshot" -> {
                            MinecraftClient.getInstance().execute(() -> {
                                ScreenshotUtils.takeScreenshotAsync(path -> {
                                    if (path.isEmpty()) return;
                                    HashMap<String, String> response = new HashMap<>();
                                    response.put("timestamp", String.valueOf(System.currentTimeMillis()));
                                    response.put("answer", path);
                                    try {
                                        FilesHandler.writeToFile("bot/sharedForBot.json", new JSONObject(response).toString(4));
                                        clearRequest();
                                    } catch (IOException e) {
                                        throw new RuntimeException(e);
                                    }
                                });
                            });
                        }
                        case "say" -> {
                            Chat.say(json.getString("content"));
                            HashMap<String, String> response = new HashMap<>();
                            response.put("timestamp", String.valueOf(System.currentTimeMillis()));
                            response.put("answer", "done");
                            try {
                                FilesHandler.writeToFile("bot/sharedForBot.json", new JSONObject(response).toString(4));
                                clearRequest();
                            } catch (IOException e) {
                                throw new RuntimeException(e);
                            }
                        }
                        case "command" -> {
                            Chat.command(json.getString("content"));
                            HashMap<String, String> response = new HashMap<>();
                            response.put("timestamp", String.valueOf(System.currentTimeMillis()));
                            response.put("answer", "done");
                            try {
                                FilesHandler.writeToFile("bot/sharedForBot.json", new JSONObject(response).toString(4));
                                clearRequest();
                            } catch (IOException e) {
                                throw new RuntimeException(e);
                            }
                        }
                    }
                }
            } catch (IOException e) {
                e.printStackTrace();
            }

            try {
                Thread.sleep(1000);
            } catch (InterruptedException e) {
                e.printStackTrace();
            }
        }
    });

    public static void register() {
        try {
            FilesHandler.createNewFolder("bot");
            FilesHandler.createFile("bot/bot.log");
            FilesHandler.createFile("bot/stop.txt");
            FilesHandler.createFile("bot/token.txt");
            FilesHandler.createFile("bot/sharedForMod.json");
            FilesHandler.createFile("bot/sharedForBot.json");
        } catch (IOException e) {
            throw new RuntimeException(e);
        }

        try {
            String[] resources = {"javaConnection.py", "JsonCreator.py"};

            File targetDir = new File("config/coresmodule/bot");
            if (!targetDir.exists()) targetDir.mkdirs();

            for (String resourceName : resources) {
                InputStream is = Bot.class.getResourceAsStream("/" + resourceName);
                if (is == null) {
                    Helper.print(resourceName + " not found in resources!");
                    continue;
                }

                File targetFile = new File(targetDir, resourceName);
                Files.copy(is, targetFile.toPath(), StandardCopyOption.REPLACE_EXISTING);
                is.close();

                Helper.print(resourceName + " copied to " + targetFile.getAbsolutePath());
            }

        } catch (IOException e) {
            e.printStackTrace();
        }


        Register.command("setBotToken", args -> {
            try {
                FilesHandler.writeToFile("bot/token.txt", args[0]);
            } catch (IOException e) {
                throw new RuntimeException(e);
            }
            Chat.chat("§6[Cm] §aBot token set successfully!");
        });

        Register.command("connectToBot", args -> {
            if (botProcess != null && botProcess.isAlive()) {
                Chat.chat("§6[Cm] §cBot is already running.");
                return;
            }
            try {
                FilesHandler.writeToFile("bot/stop.txt", "false");

                String pythonExe = "python";
                String scriptPath = "config/coresmodule/bot/javaConnection.py";

                ProcessBuilder builder = new ProcessBuilder(pythonExe, scriptPath);
                builder.redirectErrorStream(true);
                builder.redirectOutput(new File("config/coresmodule/bot/bot.log"));
                Process process = builder.start();
                thread.start();

                botProcess = process;

                Chat.chat("§6[Cm] §aConnected to bot successfully!");

            } catch (IOException e) {
                e.printStackTrace();
                Chat.chat("§6[Cm] §cError connecting bot. Check python path: " + e.getMessage());
            }
        });

        Register.command("disconnectFromBot", args -> {
            if (botProcess == null || !botProcess.isAlive()) {
                Chat.chat("§6[Cm] §cBot is not running.");
                return;
            }
            try {
                FilesHandler.writeToFile("bot/stop.txt", "true");
                Chat.chat("§6[Cm] §eSent shutdown signal to Python. Waiting for termination...");

                new Thread(() -> {
                    try {
                        if (botProcess.waitFor(5, TimeUnit.SECONDS)) {
                            thread.interrupt();
                            Chat.chat("§6[Cm] §aBot shut down gracefully.");
                        } else {
                            botProcess.destroyForcibly();
                            thread.interrupt();
                            Chat.chat("§6[Cm] §eBot was forcefully terminated.");
                        }

                        botProcess = null;

                    } catch (Exception e) {
                        e.printStackTrace();
                        Chat.chat("§6[Cm] §cError while disconnecting from bot: " + e.getMessage());
                    }
                }).start();

            } catch (Exception e) {
                e.printStackTrace();
                Chat.chat("§6[Cm] §cError while disconnecting from bot: " + e.getMessage());
            }
        });

        Runtime.getRuntime().addShutdownHook(new Thread(() -> {
            Helper.print("Game closing, stopping bot...");
            try {
                FilesHandler.writeToFile("bot/stop.txt", "true");
            } catch (IOException e) {
                e.printStackTrace();
            }
        }));
    }

    public static void clearRequest() throws IOException {
        FilesHandler.writeToFile("bot/sharedForMod.json","{}");
    }

}

package com.me.coresmodule.features.Diana;

import com.me.coresmodule.settings.categories.Diana;
import com.me.coresmodule.utils.Helper;
import com.me.coresmodule.utils.TextHelper;
import com.me.coresmodule.utils.chat.Chat;
import com.me.coresmodule.utils.events.Register;
import net.fabricmc.fabric.api.client.screen.v1.ScreenEvents;
import net.fabricmc.fabric.api.client.screen.v1.ScreenMouseEvents;
import net.minecraft.client.gui.screen.ChatScreen;
import net.minecraft.text.Text;

import java.util.List;
import java.util.regex.Pattern;

public class AlwaysRightSphinxQuestion {
    public static List<String> correctAnswers = List.of(
            "Slime",
            "Shark",
            "Mushroom Desert",
            "Roddy",
            "Ruby",
            "Divine",
            "Dark Auction",
            "Hoppity",
            "7",
            "Zombie",
            "Vacuum",
            "Marigold",
            "Prismite",
            "Junk",
            "Backwater Bayou"
    );

    public static Text aText, bText, cText;
    public static int r = -1;
    public static boolean timerOn = false;

    public static void register() {
        Register.onChatMessageCancelable(
            Pattern.compile("^§7 {3}([ABC])\\) §f(.*?)$"),
            (msg, matcher) -> {
                if (!Diana.SphinxQuestion.get()) return false;
                timer();
                String possibleAnswer = matcher.group(2).trim();
                msg = TextHelper.stringToText(
                        TextHelper.getFormattedString(msg).replaceAll("§f", correctAnswers.contains(possibleAnswer) ? "§a" : "§c"),
                        TextHelper.stringToClickEvent(""),
                        TextHelper.getFullHoverEvent(msg)
                );
                switch (matcher.group(1)) {
                    case "A" -> {
                        r = correctAnswers.contains(possibleAnswer) ? 0 : r;
                        aText = msg;
                    }
                    case "B" -> {
                        r = correctAnswers.contains(possibleAnswer) ? 1 : r;
                        bText = msg;
                    }
                    case "C" -> {
                        r = correctAnswers.contains(possibleAnswer) ? 2 : r;
                        cText = msg;
                    }
                }

                return true;
            }
        );

        ScreenEvents.AFTER_INIT.register((client, screen, scaledWidth, scaledHeight) -> {
            if (screen instanceof ChatScreen && Diana.SphinxQuestion.get()) {
                ScreenMouseEvents.beforeMouseClick(screen).register((s, mouseX, mouseY, button) -> {
                    if (button == 0 && r != -1) {
                        Chat.command("/sphinxanswer " + r);
                        r = -1;
                    }
                });
            }
        });
    }

    public static void timer() {
        if (timerOn) return;
        timerOn = true;
        Helper.sleep(100, () -> {
            for (Text msg : new Text[] {aText, bText, cText}) {
                Chat.chat(
                        TextHelper.stringToText(
                                TextHelper.getFormattedString(msg),
                                TextHelper.stringToClickEvent("/sphinxanswer " + r),
                                TextHelper.getFullHoverEvent(msg)
                        )
                );
            }
           timerOn = false;
        });
    }
}

package com.me.coresmodule.features.Diana;

import com.me.coresmodule.utils.Helper;
import com.me.coresmodule.utils.ItemHelper;
import com.me.coresmodule.utils.chat.Chat;
import com.me.coresmodule.utils.events.Register;
import com.me.coresmodule.utils.render.overlay.Overlay;
import com.me.coresmodule.utils.render.overlay.OverlayTextLine;
import net.fabricmc.fabric.api.event.player.UseBlockCallback;
import net.fabricmc.fabric.api.event.player.UseItemCallback;
import net.minecraft.item.ItemStack;
import net.minecraft.util.ActionResult;

import java.util.List;

public class DianaFeatures {
    static boolean ffTimerOn = false;
    static long startTime = -1;
    static long endTime = -1;
    static double remaining = -1;

    public static void register() {
        Overlay overlay = new Overlay("Fire Freeze Timer", 10.0f, 10.0f, 2.0f, List.of("Chat screen"));
        OverlayTextLine overlayText = new OverlayTextLine("");
        overlay.register();
        overlay.setCondition(() -> ffTimerOn);
        overlay.addLine(overlayText);

        Register.command("kingWarning", args -> {
            Helper.sleep(200, () -> {
                Chat.command("pc ⚠ Do NOT use Ranged/Magic damage on Kings // No Flaming Flay / No Term / No Crimson Stacks (Dominus) // Switch to Sorrow/Mythos armor and do the hit phase with melee only ⚠");
            });
        });

        Register.command("kingSpawn", args -> {
            Helper.sleep(200, () -> {
                Chat.command("pc Hi old man, give me your wool or I’m selling your brother’s soul to the devil—and yours too if I manage to grab it >:)");
            });
        });

        UseItemCallback.EVENT.register((player, world, hand) -> {
			if (ffTimerOn || remaining >= 0) return ActionResult.PASS;
            ItemStack item = player.getMainHandStack();
            if (ItemHelper.getItemName(item).contains("Fire Freeze Staff")) {
                ffTimerOn = true;
                startTime = System.currentTimeMillis();
                endTime = startTime + 10000;
            }

            return ActionResult.PASS;
        });

        UseBlockCallback.EVENT.register((player, world, hand, blockHitResult) -> {
			if (ffTimerOn || remaining >= 0) return ActionResult.PASS;
            ItemStack item = player.getMainHandStack();
            if (ItemHelper.getItemName(item).contains("Fire Freeze Staff")) {
                ffTimerOn = true;
                startTime = System.currentTimeMillis();
                endTime = startTime + 10000;
            }

            return ActionResult.PASS;
        });

        Register.onTick(1, args -> {
            if (!ffTimerOn) return;

            long now = System.currentTimeMillis();
            remaining = (endTime - now) / 1000.0;

            if (remaining <= -5) { // Stops below -5s, between 5s and -5s is the time during which the mob is freezed
                ffTimerOn = false;
                remaining = -1;
                startTime = -1;
                endTime = -1;
                return;
            }

            overlayText.text = "%s%.2fs".formatted(remaining <= 0 ? "§c" : "§a", remaining);
        });
    }
}

package com.me.coresmodule.features.Diana;

import com.me.coresmodule.utils.Helper;
import com.me.coresmodule.utils.TextHelper;
import com.me.coresmodule.utils.chat.Chat;
import com.me.coresmodule.utils.events.Register;

import java.util.regex.Matcher;
import java.util.regex.Pattern;

public class MfCalc {
    public static void register() {
        Register.command("mymf", args -> {
            if (args.length > 2 || args.length == 0) {
                Chat.chat("§cUsage: §6/mymf §b<number> §a(Go in mf set with no1 around (so legion ISNT active) with renowned armor, it will give your mf if you used shuriken, fragged dae axe with max bestiary)");
            } else {
                double mf = Double.parseDouble(args[0]);
                double kc = (args.length == 1 ? 0 : Double.parseDouble(args[1]));
                Chat.chat("§6§l[Cm] Your magic find is §b§l%.2f §6§lon Inquisitors".formatted(mymf(mf, kc)));
            }
        });

        Register.onChatMessage(message -> {
            Pattern regexWith2Numbers = Pattern.compile("^Party > \\[?[^\\]]*\\]?\\s*(\\w+): !mymf (\\d+) (\\d+)$"); // Matches "!mymf <number> <number>"
            Pattern regexWithNumber = Pattern.compile("^Party > \\[?[^\\]]*\\]?\\s*(\\w+): !mymf (\\d+)$"); // Matches "!mymf <number>"
            Pattern regexWithoutNumber = Pattern.compile("^Party > \\[?[^\\]]*\\]?\\s*(\\w+): !mymf$"); // Matches only "!mymf"

            String msg = TextHelper.removeFormatting(message.getString());

            if (!regexWithNumber.matcher(msg).find() && !regexWithoutNumber.matcher(msg).find() && !regexWith2Numbers.matcher(msg).find()) return;

            Matcher match1 = regexWithNumber.matcher(msg);
            Matcher match2 = regexWith2Numbers.matcher(msg);

            if (match1.find()) {
                double number = Double.parseDouble(match1.group(2));
                double mf = mymf(number, 0);
                Helper.sleep(750, () -> {
                    Chat.command("pc [Cm] Your magic find is " + String.format("%.2f", mf) + " on Inquisitors");
                });
            } else if (match2.find()) {
                double number = Double.parseDouble(match2.group(2));
                double kc = Double.parseDouble(match2.group(3));
                double mf = mymf(number, kc);
                Helper.sleep(750, () -> {
                    Chat.command("pc [Cm] Your magic find is " + String.format("%.2f", mf) + " on Inquisitors");
                });
            } else {
                Helper.sleep(750, () -> {
                    Chat.command("pc Usage: !mymf <number> (Go in mf set with no1 around (so legion ISNT active) with renowned armor, it will give your mf if you used shuriken, fragged dae axe with max bestiary)");
                });
            }
        });
    }

    public static double mymf(double mf, double kc) {
        kc = (kc == 0 ? 1 : 1 + (kc / 100));
        mf = mf / 1.04;
        mf = ((mf*1.11/* Renowned + Legion */)*1.05 /* Shuriken */+ (130 /* Max Bestiary */ * 1.05 /* Shuriken */) * kc);
        return mf;
    }
}

package com.me.coresmodule.features.Diana;

import com.me.coresmodule.utils.ItemHelper;
import com.me.coresmodule.utils.events.EventBus.EventBus;
import com.me.coresmodule.utils.events.Register;
import com.me.coresmodule.utils.math.CmVectors;
import net.fabricmc.fabric.api.client.event.lifecycle.v1.ClientEntityEvents;
import net.minecraft.client.MinecraftClient;
import net.minecraft.client.network.AbstractClientPlayerEntity;
import net.minecraft.client.world.ClientWorld;
import net.minecraft.entity.Entity;
import net.minecraft.entity.decoration.ArmorStandEntity;
import net.minecraft.item.ItemStack;

import java.util.*;
import java.util.concurrent.ConcurrentHashMap;
import java.util.function.Function;
import java.util.function.Predicate;
import java.util.regex.Pattern;

import static com.me.coresmodule.CoresModule.mc;
import static com.me.coresmodule.utils.TextHelper.formattedString;

public class MfCalcHelper {
    private static final Map<Integer, ArmorStandEntity> trackedEntities = new HashMap<>();
    private static final Set<Integer> defeated = new HashSet<>();
    private static final EventBus EVENT_BUS = new EventBus();

    public static int playersInLegion() {
        AbstractClientPlayerEntity player = mc.player;
        return mc.world == null ? 0 :
                mc.world.getPlayers()
                        .stream()
                        .filter(otherPlayer ->
                                otherPlayer != player &&
                                        (otherPlayer.getUuid().version() == 4 || otherPlayer.getUuid().version() == 1) &&
                                        getPlayerPing(mc, otherPlayer.getUuid()) > 0 &&
                                        otherPlayer.distanceTo(player) <= 30
                        )
                        .filter(distinctByKey(p -> p.getUuid()))
                        .toList()
                        .size();
    }

    public static <T> Predicate<T> distinctByKey(Function<? super T, ?> keyExtractor) {
        Set<Object> seen = ConcurrentHashMap.newKeySet();
        return t -> seen.add(keyExtractor.apply(t));
    }

    private static int getPlayerPing(MinecraftClient mc, UUID uuid) {
        if (
                mc.getNetworkHandler() == null ||
                mc.getNetworkHandler().getPlayerListEntry(uuid) == null
        ) return 0;

        return Objects.requireNonNull(mc.getNetworkHandler().getPlayerListEntry(uuid)).getLatency();
    }

    public static int renownedAmt() {
        int count = 0;
        List<ItemStack> armor = ItemHelper.getArmorItems();
        for (ItemStack item : armor) {
            String name = ItemHelper.getItemName(item);
            if (name.contains("Renowned")) {
                count++;
            }
        }
        return count;
    }

    public static double mfFromMythos() {
        double count = 0.0;
        List<ItemStack> armor = ItemHelper.getArmorItems();
        for (ItemStack item : armor) {
            String name = ItemHelper.getItemName(item);
            if (name.contains("Mythos")) {
                count++;
            }
        }
        if (count <= 1) return 0;
        return 5 * count;
    }

    public static double mfFromHand() {
        String mf1;
        String mf2;
        String amt;

        // Get the Magic Find: +x
        mf1 = !Objects.equals(amt = ItemHelper.getValueFromLine(Pattern.compile("Magic Find: \\+(.*)"), ItemHelper.getHeldItemTooltip()), "") && !amt.isEmpty() ? amt : "0.0";
        // Get the +x✯ Magic Find (be tiers)
        mf2 = !Objects.equals(amt = ItemHelper.getValueFromLine(Pattern.compile("\\+([0-9]*\\.?[0-9]+)✯ Magic Find"), ItemHelper.getHeldItemTooltip()), "") && !amt.isEmpty() ? amt : "0.0";
        double mf = Double.parseDouble(mf1) + Double.parseDouble(mf2);
        return mf;
    }

    public static double mfFromArmor() {
        List<ItemStack> armor = ItemHelper.getArmorItems();
        String mf1;
        String mf2;
        String mf3;
        String mf4;
        String mf5;
        String amt;

        mf1 = !Objects.equals(amt = ItemHelper.getValueFromLine(Pattern.compile("Magic Find: \\+(.*)"), ItemHelper.getItemTooltip(armor.get(0))), "") && !amt.isEmpty() ? amt : "0.0";
        mf2 = !Objects.equals(amt = ItemHelper.getValueFromLine(Pattern.compile("Magic Find: \\+(.*)"), ItemHelper.getItemTooltip(armor.get(1))), "") && !amt.isEmpty() ? amt : "0.0";
        mf3 = !Objects.equals(amt = ItemHelper.getValueFromLine(Pattern.compile("Magic Find: \\+(.*)"), ItemHelper.getItemTooltip(armor.get(2))), "") && !amt.isEmpty() ? amt : "0.0";
        mf4 = !Objects.equals(amt = ItemHelper.getValueFromLine(Pattern.compile("Magic Find: \\+(.*)"), ItemHelper.getItemTooltip(armor.get(3))), "") && !amt.isEmpty() ? amt : "0.0";
        mf5 = !Objects.equals(amt = ItemHelper.getValueFromLine(Pattern.compile("\\+([0-9]*\\.?[0-9]+)✯ Magic Find ✿"), ItemHelper.getItemTooltip(armor.get(3))), "") && !amt.isEmpty() ? amt : "0.0";

        double mf = Double.parseDouble(mf1) + Double.parseDouble(mf2) + Double.parseDouble(mf3) + Double.parseDouble(mf4) + Double.parseDouble(mf5) + mfFromMythos();
        return mf;
    }

    /*
     * Shuriken Part
     */


    public static void register() {
        ClientEntityEvents.ENTITY_LOAD.register((entity, world) -> {
            if (entity instanceof ArmorStandEntity armorstand) {
                trackedEntities.put(entity.getId(), armorstand);
            }
        });

        ClientEntityEvents.ENTITY_UNLOAD.register((entity, world) -> {
            if (entity instanceof ArmorStandEntity living) {
                trackedEntities.remove(entity.getId());
                defeated.remove(entity.getId());
                NewMfCalc.shuriken = false;
            }
        });

        Register.onTick(1, entity -> {
            ClientWorld world = MinecraftClient.getInstance().world;
            Iterator<Map.Entry<Integer, ArmorStandEntity>> iterator = trackedEntities.entrySet().iterator();
            while (iterator.hasNext()) {
                Map.Entry<Integer, ArmorStandEntity> entry = iterator.next();
                Integer id = entry.getKey();
                ArmorStandEntity armorStand = entry.getValue();

                if (!armorStand.isAlive() || armorStand.getWorld() != world) {
                    iterator.remove();
                    defeated.remove(id);
                    continue;
                }
                checkEntity(armorStand, id);
            }
        });
    }

    private static boolean hasShuriken(String name) {
        /*
        List<String> shuriNames = List.of(
            "creatan bull",
            "harpy",
            "nymph",
            "sphinx",
            "gaia construct",
            "manticore",
            "minos hunter",
            "minos champion",
            "minos inquisitor",
            "king minos",
            "siamese lynx"
        );

         */
        return name.contains("✯")/*&& shuriNames.contains(name.toLowerCase())*/;
    }

    private static void checkEntity(ArmorStandEntity entity, Integer id) {
        String name = entity.getCustomName() != null
                ? formattedString(entity.getCustomName())
                : formattedString(entity.getName());

        if (!defeated.contains(id) && hasShuriken(name)) {
            NewMfCalc.shuriken = true;
        }
    }
}


