package com.me.coresmodule.utils.render.overlay;

import com.me.coresmodule.utils.Helper;
import net.minecraft.client.gui.DrawContext;

import java.awt.*;
import java.util.ArrayList;
import java.util.List;
import java.util.function.Supplier;

import static com.me.coresmodule.CoresModule.mc;

public class Overlay {

    String name;
    float x;
    float y;
    float scale;
    List<String> allowedGuis;
    private List<OverlayTextLine> exampleView;
    private List<OverlayTextLine> lines = new ArrayList<>();
    private Supplier<Boolean> condition = () -> true;
    public boolean selected = false;

    public Overlay(String name, float x, float y) {
        this(name, x, y, 1.0f, List.of("Chat screen"), List.of());
    }

    public Overlay(String name, float x, float y, float scale) {
        this(name, x, y, scale, List.of("Chat screen"), List.of());
    }

    public Overlay(String name, float x, float y, float scale, List<String> allowedGuis) {
        this(name, x, y, scale, allowedGuis, List.of());
    }

    public Overlay(String name, float x, float y, float scale, List<String> allowedGuis, List<OverlayTextLine> exampleView) {
        this.name = name;
        this.x = x;
        this.y = y;
        this.scale = scale;
        this.allowedGuis = allowedGuis;
        this.exampleView = exampleView;
    }

    public void register() {
        OverlayData.register();
        if (OverlayData.overlays.containsKey(name)) {
            OverlayValues data = (OverlayValues) OverlayData.overlays.get(name);
            x = data.x;
            y = data.y;
            scale = data.scale;
        } else {
            OverlayData.overlays.put(name, new OverlayValues(x, y, scale));
        }
        OverlayManager.overlays.add(this);
    }

    public Overlay setCondition(Supplier<Boolean> condition) {
        this.condition = condition;
        return this;
    }

    public boolean checkCondition() {
        return condition.get();
    }

    public void addLine(OverlayTextLine line) {
        lines.add(line);
    }

    public void addLineAt(int index, OverlayTextLine line) {
        lines.add(index, line);
    }

    public void addLines(List<OverlayTextLine> newLines) {
        lines.addAll(newLines);
    }

    public void setLines(List<OverlayTextLine> newLines) {
        lines = new ArrayList<>(newLines);
    }

    public void removeLine(OverlayTextLine line) {
        lines.remove(line);
    }

    public void removeLines(List<OverlayTextLine> toRemove) {
        lines.removeAll(toRemove);
    }

    public void clearLines() {
        lines = new ArrayList<>();
    }

    public List<OverlayTextLine> getLines() {
        if (lines.isEmpty() && !exampleView.isEmpty() && mc.currentScreen instanceof OverlayEditScreen) {
            return exampleView;
        }
        return lines;
    }

    public void overlayClicked(double mouseX, double mouseY) {
        if (!allowedGuis.contains(Helper.getGuiName())) return;
        var textRenderer = mc.textRenderer;
        if (textRenderer == null) return;
        if (!isOverOverlay(mouseX, mouseY)) return;

        float currentY = y / scale;
        float currentX = x / scale;

        for (OverlayTextLine line : getLines()) {
            if (!line.checkCondition()) continue;

            line.lineClicked(mouseX, mouseY, currentX * scale, currentY * scale, textRenderer, scale);

            if (line.linebreak) {
                currentY += textRenderer.fontHeight + 1;
                currentX = x / scale;
            } else {
                currentX += textRenderer.getWidth(line.text) / scale;
            }
        }
    }

    public int getTotalHeight() {
        var textRenderer = mc.textRenderer;
        if (textRenderer == null) return 0;

        int totalHeight = 0;
        for (OverlayTextLine line : getLines()) {
            if (line.linebreak && line.checkCondition()) {
                totalHeight += textRenderer.fontHeight + 1;
            }
        }
        return totalHeight;
    }

    public int getTotalWidth() {
        var textRenderer = mc.textRenderer;
        if (textRenderer == null) return 0;

        int maxWidth = 0;
        int currentWidth = 0;

        for (OverlayTextLine line : getLines()) {
            currentWidth += textRenderer.getWidth(line.text);
            if (line.linebreak) {
                if (currentWidth > maxWidth) maxWidth = currentWidth;
                currentWidth = 0;
            }
        }

        if (currentWidth > maxWidth) maxWidth = currentWidth;
        return maxWidth;
    }

    public boolean isOverOverlay(double mouseX, double mouseY) {
        if (!condition.get()) return false;
        float totalWidth = getTotalWidth() * scale;
        float totalHeight = getTotalHeight() * scale;

        return mouseX >= x && mouseX <= x + totalWidth && mouseY >= y && mouseY <= y + totalHeight;
    }

    public void render(DrawContext drawContext, double mouseX, double mouseY) {
        if (!condition.get()) return;
        var textRenderer = mc.textRenderer;
        if (textRenderer == null) return;

        drawContext.getMatrices().push();
        drawContext.getMatrices().scale(scale, scale, 1.0f);

        float currentY = y / scale;
        float currentX = x / scale;

        int totalWidth = getTotalWidth();
        int totalHeight = getTotalHeight();

        if (selected) {
            drawDebugBox(drawContext, (int) currentX, (int) currentY, totalWidth, totalHeight);
            drawContext.drawText(textRenderer, "X: " + (int) x + " Y: " + (int) y + " Scale: " + String.format("%.1f", scale), (int) currentX, (int) (currentY - textRenderer.fontHeight - 1), new Color(255, 255, 255, 200).getRGB(), true);
        }

        if (isOverOverlay(mouseX, mouseY) && mc.currentScreen instanceof OverlayEditScreen) {
            drawContext.fill((int) currentX, (int) currentY, (int) (currentX + totalWidth), (int) (currentY + totalHeight), new Color(0, 0, 0, 100).getRGB());
        }

        for (OverlayTextLine line : getLines()) {
            if (!line.checkCondition()) continue;

            if (allowedGuis.contains(Helper.getGuiName())) {
                line.updateMouseInteraction(mouseX, mouseY, currentX * scale, currentY * scale, textRenderer, scale, drawContext);
            }

            line.draw(drawContext, (int) currentX, (int) currentY, textRenderer);

            if (line.linebreak) {
                currentY += textRenderer.fontHeight + 1;
                currentX = x / scale;
            } else {
                currentX += textRenderer.getWidth(line.text);
            }
        }

        drawContext.getMatrices().pop();
    }

    private void drawDebugBox(DrawContext drawContext, int x, int y, int width, int height) {
        drawContext.drawBorder(x, y, width, height, new Color(255, 0, 0, 170).getRGB());
    }
}

package com.me.coresmodule.utils.render.overlay;

import com.me.coresmodule.utils.FilesHandler;
import net.fabricmc.fabric.api.client.event.lifecycle.v1.ClientLifecycleEvents;
import net.fabricmc.fabric.api.client.networking.v1.ClientPlayConnectionEvents;
import org.json.JSONObject;

import java.io.IOException;
import java.util.HashMap;
import java.util.Map;

public class OverlayData {
    public static Map<String, OverlayValues> overlays = new HashMap<>();

    public static void register() {
        try {
            FilesHandler.createFile("overlays.json");
        } catch (IOException e) {
            throw new RuntimeException(e);
        }

        load();

        ClientLifecycleEvents.CLIENT_STOPPING.register(client -> save());

        ClientPlayConnectionEvents.DISCONNECT.register((handler, client) -> save());
    }

    public static void save() {
        try {
            Map<String, Object> toSave = new HashMap<>();
            for (String key : overlays.keySet()) {
                toSave.put(key, overlays.get(key).toMap());
            }
            JSONObject json = new JSONObject(toSave);
            FilesHandler.writeToFile("overlays.json", json.toString(4));
        } catch (IOException e) {
            throw new RuntimeException(e);
        }
    }

    public static void load() {
        try {
            String content = FilesHandler.getContent("overlays.json").trim();
            if (!content.isEmpty() && !content.equals("{}")) {
                JSONObject json = new JSONObject(content);
                Map<String, Object> temp = json.toMap();
                for (String key : temp.keySet()) {
                    @SuppressWarnings("unchecked") Map<String, Object> values = (Map<String, Object>) temp.get(key);
                    float x = ((Number) values.get("x")).floatValue();
                    float y = ((Number) values.get("y")).floatValue();
                    float scale = ((Number) values.get("scale")).floatValue();
                    overlays.put(key, new OverlayValues(x, y, scale));
                }
            } else {
                FilesHandler.writeToFile("overlays.json", "{}");
            }
        } catch (IOException e) {
            throw new RuntimeException(e);
        }
    }
}

package com.me.coresmodule.utils.render.overlay;

import net.minecraft.client.gui.DrawContext;
import net.minecraft.client.gui.screen.Screen;
import net.minecraft.text.Text;
import org.lwjgl.glfw.GLFW;

public class OverlayEditScreen extends Screen {

    private Overlay selectedOverlay = null;
    private boolean isDragging = false;
    private double lastMouseX = 0.0;
    private double lastMouseY = 0.0;

    public OverlayEditScreen() {
        super(Text.literal("CM_Overlay_Editor"));
    }

    @Override
    public void render(DrawContext context, int mouseX, int mouseY, float delta) {
        super.render(context, mouseX, mouseY, delta);
        this.renderDarkening(context);
        OverlayManager.render(context);
    }

    @Override
    public boolean mouseClicked(double mouseX, double mouseY, int button) {
        if (button == 0) {
            selectedOverlay = OverlayManager.overlays.stream()
                    .filter(o -> o.isOverOverlay(mouseX, mouseY))
                    .findFirst()
                    .orElse(null);

            if (selectedOverlay != null) {
                isDragging = true;
                lastMouseX = mouseX;
                lastMouseY = mouseY;

                for (Overlay o : OverlayManager.overlays) {
                    o.selected = (o == selectedOverlay);
                }
            } else {
                for (Overlay o : OverlayManager.overlays) {
                    o.selected = false;
                }
            }
            return true;
        }
        return super.mouseClicked(mouseX, mouseY, button);
    }

    @Override
    public boolean mouseDragged(double mouseX, double mouseY, int button, double deltaX, double deltaY) {
        if (isDragging && selectedOverlay != null) {
            selectedOverlay.x = selectedOverlay.x + (float) deltaX;
            OverlayData.overlays.get(selectedOverlay.name).x = selectedOverlay.x;
            selectedOverlay.y = selectedOverlay.y + (float) deltaY;
            OverlayData.overlays.get(selectedOverlay.name).y = selectedOverlay.y;
            return true;
        }
        return super.mouseDragged(mouseX, mouseY, button, deltaX, deltaY);
    }

    @Override
    public boolean mouseReleased(double mouseX, double mouseY, int button) {
        if (button == 0) {
            isDragging = false;
            return true;
        }
        return super.mouseReleased(mouseX, mouseY, button);
    }

    @Override
    public boolean mouseScrolled(double mouseX, double mouseY, double horizontalAmount, double verticalAmount) {
        if (selectedOverlay != null) {
            float newScale = (float) (selectedOverlay.scale + verticalAmount * 0.1f);
            if (newScale < 0.5f) newScale = 0.5f;
            if (newScale > 5.0f) newScale = 5.0f;
            selectedOverlay.scale = newScale;
            OverlayData.overlays.get(selectedOverlay.name).scale = newScale;
            return true;
        }
        return super.mouseScrolled(mouseX, mouseY, horizontalAmount, verticalAmount);
    }

    @Override
    public boolean keyPressed(int keyCode, int scanCode, int modifiers) {
        if (selectedOverlay != null) {
            float step = 1f;
            if (keyCode == GLFW.GLFW_KEY_UP) {
                selectedOverlay.y -= step;
                OverlayData.overlays.get(selectedOverlay.name).y = selectedOverlay.y;
            } else if (keyCode == GLFW.GLFW_KEY_DOWN) {
                selectedOverlay.y += step;
                OverlayData.overlays.get(selectedOverlay.name).y = selectedOverlay.y;
            } else if (keyCode == GLFW.GLFW_KEY_LEFT) {
                selectedOverlay.x -= step;
                OverlayData.overlays.get(selectedOverlay.name).x = selectedOverlay.x;
            } else if (keyCode == GLFW.GLFW_KEY_RIGHT) {
                selectedOverlay.x += step;
                OverlayData.overlays.get(selectedOverlay.name).x = selectedOverlay.x;
            } else {
                return super.keyPressed(keyCode, scanCode, modifiers);
            }
            return true;
        }
        return super.keyPressed(keyCode, scanCode, modifiers);
    }

    @Override
    public void removed() {
        super.removed();
        for (Overlay o : OverlayManager.overlays) {
            o.selected = false;
        }
        OverlayData.save();
    }
}

package com.me.coresmodule.utils.render.overlay;

import com.me.coresmodule.utils.events.EventBus.EventBus;
import com.me.coresmodule.utils.events.Register;
import net.fabricmc.fabric.api.client.screen.v1.ScreenEvents;
import net.fabricmc.fabric.api.client.screen.v1.ScreenMouseEvents;
import net.minecraft.client.gui.DrawContext;
import net.minecraft.client.gui.screen.Screen;

import java.util.ArrayList;
import java.util.List;

import static com.me.coresmodule.CoresModule.mc;

public final class OverlayManager {

    public static final List<Overlay> overlays = new ArrayList<>();

    private OverlayManager() {
    }

    public static void register() {
        registerRenderer();
        registerMouseLeftClick();

        Register.command("cmguis", args -> {
            mc.send(() -> mc.setScreen(new OverlayEditScreen()));
        });

        EventBus.on("afterHudRender", obj -> {
            DrawContext context = (DrawContext) obj;

            String title = mc.currentScreen != null
                    ? mc.currentScreen.getTitle().getString()
                    : "";

            render(context, title);
        });
    }

    public static void render(DrawContext drawContext, String renderScreen) {
        double scaleFactor = mc.getWindow().getScaleFactor();
        double mouseX = mc.mouse.getX() / scaleFactor;
        double mouseY = mc.mouse.getY() / scaleFactor;

        for (Overlay overlay : List.copyOf(overlays)) {
            if (renderScreen.isEmpty()) {
                overlay.render(drawContext, mouseX, mouseY);
            }
        }
    }

    public static void render(DrawContext drawContext) {
        render(drawContext, "");
    }

    public static void postRender(DrawContext drawContext, Screen renderScreen) {
        double scaleFactor = mc.getWindow().getScaleFactor();
        double mouseX = mc.mouse.getX() / scaleFactor;
        double mouseY = mc.mouse.getY() / scaleFactor;

        for (Overlay overlay : List.copyOf(overlays)) {
            if (overlay.allowedGuis.contains(renderScreen.getTitle().getString())) {
                overlay.render(drawContext, mouseX, mouseY);
            }
        }
    }

    public static void registerRenderer() {
        ScreenEvents.AFTER_INIT.register((client, screen, scaledWidth, scaledHeight) -> {
            ScreenEvents.afterRender(screen).register((renderScreen, drawContext, mouseX, mouseY, tickDelta) -> {
                if (!(renderScreen instanceof OverlayEditScreen)) {
                    postRender(drawContext, renderScreen);
                }
            });
        });
    }

    public static void registerMouseLeftClick() {
        ScreenEvents.AFTER_INIT.register((client, screen, scaledWidth, scaledHeight) -> {
            ScreenMouseEvents.afterMouseClick(screen).register((clickedScreen, mouseX, mouseY, button) -> {
                if (!(clickedScreen instanceof OverlayEditScreen) && button == 0) {
                    for (Overlay overlay : overlays) {
                        overlay.overlayClicked(mouseX, mouseY);
                    }
                }
            });
        });
    }
}

package com.me.coresmodule.utils.render.overlay;

import net.minecraft.client.gui.DrawContext;
import net.minecraft.client.font.TextRenderer;
import java.awt.Color;

public class OverlayTextLine {
    public String text;
    public boolean shadow = true;
    public boolean linebreak = true;
    public Runnable mouseEnterAction = null;
    public Runnable mouseLeaveAction = null;
    public HoverAction hoverAction = null;
    public Runnable clickAction = null;
    public boolean isHovered = false;
    public int x = 0;
    public int y = 0;
    private int width = 0;
    private int height = 0;
    public boolean renderDebugBox = false;
    private Condition condition = () -> true;

    public interface HoverAction {
        void run(DrawContext drawContext, TextRenderer textRenderer);
    }

    public interface Condition {
        boolean check();
    }

    public OverlayTextLine(String text) {
        this.text = text;
    }

    public void setText(String newText) {
        this.text = newText;
    }

    public OverlayTextLine(String text, boolean shadow, boolean linebreak) {
        this.text = text;
        this.shadow = shadow;
        this.linebreak = linebreak;
    }

    public OverlayTextLine setCondition(Condition condition) {
        this.condition = condition;
        return this;
    }

    public boolean checkCondition() {
        return condition.check();
    }

    public OverlayTextLine onMouseEnter(Runnable action) {
        this.mouseEnterAction = action;
        return this;
    }

    public OverlayTextLine onMouseLeave(Runnable action) {
        this.mouseLeaveAction = action;
        return this;
    }

    public OverlayTextLine onHover(HoverAction action) {
        this.hoverAction = action;
        return this;
    }

    public OverlayTextLine onClick(Runnable action) {
        this.clickAction = action;
        return this;
    }

    private void mouseEnter() {
        if (mouseEnterAction != null) mouseEnterAction.run();
    }

    private void mouseLeave() {
        if (mouseLeaveAction != null) mouseLeaveAction.run();
    }

    private void hover(DrawContext drawContext, TextRenderer textRenderer) {
        if (isHovered && hoverAction != null) hoverAction.run(drawContext, textRenderer);
    }

    public void lineClicked(double mouseX, double mouseY, float x, float y, TextRenderer textRenderer, float scale) {
        if (text.isEmpty() || clickAction == null) return;
        if (isMouseOver(mouseX, mouseY, x, y, textRenderer, scale)) {
            clickAction.run();
        }
    }

    private boolean isMouseOver(double mouseX, double mouseY, float x, float y, TextRenderer textRenderer, float scale) {
        float textWidth = textRenderer.getWidth(text) * scale;
        float textHeight = (textRenderer.fontHeight + 1) * scale - 1;
        return mouseX >= x && mouseX <= x + textWidth && mouseY >= y && mouseY <= y + textHeight;
    }

    public void updateMouseInteraction(double mouseX, double mouseY, float x, float y, TextRenderer textRenderer, float scale, DrawContext drawContext) {
        if (text.isEmpty()) return;
        if (mouseEnterAction == null && mouseLeaveAction == null && hoverAction == null) return;

        boolean wasHovered = isHovered;
        boolean isNowHovered = isMouseOver(mouseX, mouseY, x, y, textRenderer, scale);
        isHovered = isNowHovered;

        if (isNowHovered && !wasHovered) {
            mouseEnter();
        } else if (!isNowHovered && wasHovered) {
            mouseLeave();
        }

        if (isNowHovered) {
            hover(drawContext, textRenderer);
        }
    }

    public void draw(DrawContext drawContext, int x, int y, TextRenderer textRenderer) {
        if (text.isEmpty()) return;

        this.x = x;
        this.y = y;
        this.width = textRenderer.getWidth(text);
        this.height = textRenderer.fontHeight;

        if (renderDebugBox) {
            drawContext.fill(x, y, x + width, y + height + 1, new Color(128, 128, 128, 130).getRGB());
        }

        drawContext.drawText(textRenderer, text, x, y, -1, shadow);
    }
}

package com.me.coresmodule.utils.render.overlay;

import net.minecraft.util.Formatting;

public class OverlayUtils {
    public static class LootItemData {
        public final String id;
        public final String name;
        public final Formatting color;
        public final boolean combined;
        public final String dropMobId;
        public final String dropMobLsId;
        public final boolean isRarerDrop;

        public LootItemData(
                String id,
                String name,
                Formatting color,
                boolean combined,
                String dropMobId,
                String dropMobLsId,
                boolean isRarerDrop
        ) {
            this.id = id;
            this.name = name;
            this.color = color;
            this.combined = combined;
            this.dropMobId = dropMobId;
            this.dropMobLsId = dropMobLsId;
            this.isRarerDrop = isRarerDrop;
        }
    }

    public static OverlayTextLine createClickableTextLine(
            String text,
            String hoverText,
            String defaultText,
            Runnable onClick,
            Runnable onMouseEnter,
            Runnable onMouseLeave,
            boolean lineBreak
    ) {
        if (onClick == null) onClick = () -> {};
        OverlayTextLine line = new OverlayTextLine(text, true, lineBreak).onClick(onClick);

        Runnable enterAction = onMouseEnter != null
                ? onMouseEnter
                : () -> line.setText(hoverText != null ? hoverText : text + Formatting.UNDERLINE);

        Runnable leaveAction = onMouseLeave != null
                ? onMouseLeave
                : () -> line.setText(defaultText != null ? defaultText : text);

        line.onMouseEnter(enterAction);
        line.onMouseLeave(leaveAction);

        return line;
    }
}

