        );

        float minX = (float)(-width / 2.0);
        float minY = 0f;
        float minZ = (float)(-depth / 2.0);
        float maxX = (float)(width / 2.0);
        float maxY = (float)height;
        float maxZ = (float)(depth / 2.0);

