    public static void drawString(
            WorldRenderContext context,
            CmVectors pos,
            double yOffset,
            String text,
            int color,
            boolean shadow,
            double scale,
            boolean throughWalls
    ) {
        MatrixStack matrices = context.matrixStack();
        Vec3d cameraPos = context.camera().getPos();
        float cameraYaw = context.camera().getYaw();
        float cameraPitch = context.camera().getPitch();
        TextRenderer textRenderer = mc.textRenderer;

        matrices.push();

        Vec3d textWorldPos = new Vec3d(pos.x + 0.5, pos.y + 0.5, pos.z + 0.5);
        double distance = cameraPos.distanceTo(textWorldPos);
        double dynamicScale = max(distance, 2.5) * scale;

        matrices.translate(pos.x + 0.5 - cameraPos.x, pos.y + yOffset - cameraPos.y, pos.z + 0.5 - cameraPos.z);

        matrices.multiply(RotationAxis.POSITIVE_Y.rotationDegrees(-cameraYaw));
        matrices.multiply(RotationAxis.POSITIVE_X.rotationDegrees(cameraPitch));

        matrices.scale((float)-dynamicScale, (float)-dynamicScale, (float)dynamicScale);

        int textWidth = textRenderer.getWidth(text);
        float xOffset = -textWidth / 2f;

        TextRenderer.TextLayerType layerType = throughWalls ? TextRenderer.TextLayerType.SEE_THROUGH : TextRenderer.TextLayerType.NORMAL;

        textRenderer.draw(
                text,
                xOffset,
                0f,
                color,
                shadow,
                matrices.peek().getPositionMatrix(),
                context.consumers(),
                layerType,
                0,
                0xF000F0
        );

        matrices.pop();
    }

    public static void renderBeaconBeam(
            WorldRenderContext context,
            CmVectors pos,
            int yOffset,
            float[] colorComponents
    ) {
        MatrixStack matrices = context.matrixStack();
        Vec3d cameraPos = context.camera().getPos();
        ClientWorld world = context.world();

        matrices.push();
        matrices.translate(pos.x - cameraPos.x, pos.y - cameraPos.y, pos.z - cameraPos.z);

        VertexConsumerProvider consumers = context.consumers();
        float partialTicks = context.tickCounter().getTickProgress(true);
        long worldAge = world.getTime();

        int beamHeight = context.world().getHeight();
        float[] beamColor = new float[] {colorComponents[0], colorComponents[1], colorComponents[2], 1.0f};

        BeaconBlockEntityRendererInvoker.renderBeam(
                matrices,
                consumers,
                partialTicks,
                1.0f,
                worldAge,
                yOffset,
                beamHeight,
                new Color(beamColor[0], beamColor[1], beamColor[2]).getRGB()
        );

        // TODO: Add through walls option
        matrices.pop();
    }

    public static void drawLineFromCursor(
            WorldRenderContext context,
            CmVectors target,
            float[] color,
            float lineWidth,
            boolean throughWalls,
            float alpha
    ) {
        if (alpha == 0) alpha = 0.5f;

        var camera = context.camera();
        var cameraPos = camera.getPos();
        var matrices = context.matrixStack();

        matrices.push();
        matrices.translate(-cameraPos.x, -cameraPos.y, -cameraPos.z);

        VertexConsumer buffer = context.consumers().getBuffer(CmRenderLayers.getLines(lineWidth, throughWalls));
        Vec3d startPos = cameraPos.add(Vec3d.fromPolar(camera.getPitch(), camera.getYaw()));
        Vec3d endPos = target.center().toVec3d().add(0.0, 0.5, 0.0);

        Vec3d lineDir = endPos.subtract(startPos);
        Vec3d viewDir = startPos.subtract(cameraPos);
        Vec3d sideVec = lineDir.crossProduct(viewDir).normalize();
        Vec3d upVec = sideVec.crossProduct(lineDir).normalize();

        float nx = (float) upVec.x;
        float ny = (float) upVec.y;
        float nz = (float) upVec.z;

        MatrixStack.Entry entry = matrices.peek();

        buffer.vertex(entry.getPositionMatrix(), (float) startPos.x, (float) startPos.y, (float) startPos.z)
                .normal(entry, nx, ny, nz)
                .color(color[0], color[1], color[2], alpha);

        buffer.vertex(entry.getPositionMatrix(), (float) endPos.x, (float) endPos.y, (float) endPos.z)
                .normal(entry, nx, ny, nz)
                .color(color[0], color[1], color[2], alpha);

        matrices.pop();
    }



}

package com.me.coresmodule.utils.render;

import com.me.coresmodule.utils.math.CmVectors;
import net.fabricmc.fabric.api.client.rendering.v1.WorldRenderContext;
import net.minecraft.util.math.BlockPos;

import java.awt.*;
import java.util.HashMap;

import static com.me.coresmodule.CoresModule.mc;

public class Waypoint {

    public String text;
    public double x;
    public double y;
    public double z;
    public float r;
    public float g;
    public float b;
    public int ttl;
    public String type;
    public HashMap<String, Object> typeInfo;
    public boolean line;
    public boolean beam;
    public boolean distance;
    public float lineWidth;
    public float yPlus;

    public CmVectors pos;
    public Color color;
    public int hexCode;
    public double alpha = 0.5;
    public boolean hidden = false;
    public long creation = System.currentTimeMillis();
    public boolean formatted = false;
    public double distanceRaw = 0.0;
    public String distanceText = "";
    public String formattedText = "";
    public String warp = null;

    /**
     * A class representing a waypoint in the game.
     *
     * @param text      The text to display on the waypoint.
     * @param x         The x-coordinate of the waypoint.
     * @param y         The y-coordinate of the waypoint.
     * @param z         The z-coordinate of the waypoint.
     * @param r         The red color component of the waypoint.
     * @param g         The green color component of the waypoint.
     * @param b         The blue color component of the waypoint.
     * @param ttl       The time to live for the waypoint in seconds (0 for infinite).
     * @param type      The type of the waypoint for customization.
     * @param typeInfo  The information related to the type if needed.
     * @param line      Whether to draw a line to the waypoint.
     * @param beam      Whether to draw a beam at the waypoint.
     * @param distance  Whether to display the distance to the waypoint in meters (blocks).
     * @param lineWidth The width of the line.
     * @param yPlus     How much to add to the y-coordinate of the line compared to the y-coordinate of the waypoint.
     */
    public Waypoint(String text, double x, double y, double z,
                    float r, float g, float b,
                    int ttl, String type, HashMap<String, Object> typeInfo,
                    boolean line, boolean beam, boolean distance, float lineWidth, float yPlus) {

        this.text = text;
        this.x = x;
        this.y = y;
        this.z = z;
        this.r = r;
        this.g = g;
        this.b = b;
        this.ttl = ttl;
        this.type = type;
        this.typeInfo = typeInfo;
        this.line = line;
        this.beam = beam;
        this.distance = distance;
        this.lineWidth = lineWidth;
        this.yPlus = yPlus;

        this.pos = new CmVectors(x, y, z);
        this.color = new Color(r, g, b);
        this.hexCode = this.color.getRGB();
    }

    /**
     * A class representing a waypoint in the game.
     *
     * @param text     The text to display on the waypoint.
     * @param x        The x-coordinate of the waypoint.
     * @param y        The y-coordinate of the waypoint.
     * @param z        The z-coordinate of the waypoint.
     * @param r        The red color component of the waypoint.
     * @param g        The green color component of the waypoint.
     * @param b        The blue color component of the waypoint.
     */
    public Waypoint(String text, double x, double y, double z,
                    float r, float g, float b, float lineWidth) {
        this(text, x, y, z, r, g, b, 0, "normal", new HashMap<String, Object>() , false, true, true, lineWidth, 0f);
    }

    public double distanceToPlayer() {
        BlockPos playerPos = mc.player.getBlockPos();
        return Math.sqrt(Math.pow(playerPos.getX() - this.x, 2) +
                        Math.pow(playerPos.getY() - this.y, 2) +
                        Math.pow(playerPos.getZ() - this.z, 2));

    }

    public void setText(String s) {
        this.text = s;
    }

    public void format(Object... formatters) {
        this.formattedText = String.format(this.text, formatters);
        this.formatted = true;
    }

    public void hide() {
        this.hidden = true;
    }

    public void show() {
        this.hidden = false;
    }

    public void render(WorldRenderContext context) {
        if (this.hidden) return;
        RenderUtil.renderWaypoint(
                context,
                this.formattedText,
                this.pos,
                new float[] {this.r, this.g, this.b},
                this.hexCode,
                (float) this.alpha,
                true,
                this.line,
                this.beam,
                this.lineWidth,
                this.yPlus
        );
    }
}

